mapper.select:
select DATE_FORMAT(his_time, '%Y-%m-%d %H:%i') as his_time , account_name , code_name , stock_code , format(buy_num, 0) as buy_num , format(sell_num, 0) as sell_num , format(amount, 0) as amount , format(ratio, 4) as ratio , format(profit, 0) as profit from ${account}_history where account_name = #{account}
and code_name like CONCAT('%', #{code}, '%')
and DATE_FORMAT(his_time, '%Y-%m-%d %H:%i') between '${start_date}' and '${end_date}' order by his_time desc

select DATE_FORMAT(his_time, '%Y-%m-%d') as date, code_name, stock_code
from ${account}_history
where account_name = #{account}
	and DATE_FORMAT(his_time, '%Y-%m-%d') = DATE_FORMAT(CURDATE(), '%Y-%m-%d')
group by code_name, date, stock_code
order by code_name asc;
