mapper.select:
SELECT DATE_FORMAT(dates, '%Y-%m-%d') as dates, closes, opens, highs, lows, volumes, changes
FROM ${names}
ORDER BY dates desc

SELECT DATE_FORMAT(dates, '%Y-%m-%d') as dates, opens, highs, lows, closes
FROM ${names}
ORDER BY ${base} ${sorts}

SELECT DATE_FORMAT(dates, '%Y-%m-%d') as dates, opens, highs, lows, closes
FROM ${names}
ORDER BY dates asc
